mapper.select:
SELECT list_number, list_name , member_nickname ,list_like FROM ( SELECT rownum rnum, list_number, list_name , member_nickname ,list_like FROM ( SELECT b.*, m.* FROM bookmark b INNER JOIN member m ON b.member_email = m.member_email WHERE b.list_display = 'yes'
AND ${searchCondition} LIKE '%${searchKeyword}%'
order by list_date DESC ) ) WHERE rnum between ${startRow} and ${endRow}

SELECT count(*)
FROM bookmark
WHERE member_email=#{memberEmail}

SELECT LIST_NUMBER, LIST_NAME, LIST_DISPLAY ,list_Like
FROM (
        SELECT rownum rnum, bookmark.*
        FROM (
            SELECT bookmark.*
            FROM bookmark
            WHERE member_email=#{memberEmail}
            ORDER BY LIST_NUMBER DESC
            )bookmark
            )bookmark
WHERE rnum between #{startRow} and #{endRow}

  SELECT  list_Number, list_name , list_display , list_like
