mapper.select:
SELECT *   
FROM shop s


		s.shop_food LIKE '%'||#{query}||'%' or s.shop_address LIKE '%'||#{query}||'%' or
		s.shop_title LIKE '%'||#{query}||'%'


		OR s.shop_food = #{foodKind.koreanFood}


		OR s.shop_food = #{foodKind.japaneseFood}


		OR s.shop_food = #{foodKind.chineseFood}


		OR s.shop_food = #{foodKind.westernFood}


		OR s.shop_food = #{foodKind.worldFood}


		OR s.shop_food = #{foodKind.buffet}


		OR s.shop_food = #{foodKind.cafe}


		OR s.shop_food = #{foodKind.pup}


		AND s.SHOP_ADDRESS_SI LIKE '%'||#{shopAddressSi}||'%'


		AND s.SHOP_ADDRESS_GU LIKE '%'||#{shopAddressGu}||'%'

mapper.insert:
	 	INSERT INTO shop (shop_number, shop_title, shop_tel
                     , shop_price_range, shop_food, shop_park, shop_time
                     , shop_holiday, shop_content
                     , shop_create_date, shop_modify_date
                     , shop_address, shop_address_si
                     , shop_address_gu, shop_address_dong
                     , shop_realfname
                     )
		VALUES(shop_number_seq.nextval, #{shopTitle}, #{shopTel}
		             , #{shopPriceRange, jdbcType=VARCHAR}, #{shopFood, jdbcType=VARCHAR}, #{shopPark, jdbcType=VARCHAR}, #{shopTime, jdbcType=VARCHAR}
		             , #{shopHoliday, jdbcType=VARCHAR}, #{shopContent, jdbcType=VARCHAR}
		             , sysdate, sysdate
		             , #{shopAddress}, #{shopAddressSi}
		             , #{shopAddressGu}, #{shopAddressDong}
		             , #{shopRealfname, jdbcType=VARCHAR}
		             )

mapper.update:
 	UPDATE shop
	SET shop_title=#{shopTitle}, shop_tel=#{shopTel}
	       , shop_price_range=#{shopPriceRange, jdbcType=VARCHAR}
	       , shop_food=#{shopFood, jdbcType=VARCHAR}, shop_park=#{shopPark, jdbcType=VARCHAR}
	       , shop_time=#{shopTime, jdbcType=VARCHAR}
	       , shop_holiday=#{shopHoliday, jdbcType=VARCHAR}
	       , shop_content=#{shopContent, jdbcType=VARCHAR}
	       , shop_modify_date=sysdate
	       , shop_realfname=#{shopRealfname, jdbcType=VARCHAR}
	WHERE shop_number=#{shopNumber}
